import React, {useContext} from 'react';
import style from './Cart.module.css';
import Modal from '../UI/Modal';
import CartContext from '../../store/cart-context';


const Cart = (props) => {
  const cartCtx = useContext(CartContext);

  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`
  const hasItems = cartCtx.items

const cartItems = <ul className={style['cart-items']}>{cartCtx.items.map(items => <li key={items.id} >{items.name}</li>)}</ul>;


const falseVal = () => props.onOrder(false)

  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      <div className={style.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={style.actions}>
        <button onClick={props.onClose} className={style['button--alt']}>Close</button>
        <button onClick={falseVal} className={style.button}>Order</button>
      </div>
    </Modal>
  )
}

export default Cart;
