import React from 'react';
import style from './AvailableMeals.module.css';
import Card from '../UI/Card';
import MealsItem from './MealsItem/MealsItem';
import useHttp from '../../hooks/useHttp';

// const DUMMY_MEALS = [
  // {
  //   id: 'm1',
  //   name: 'Wc-Sushi',
  //   description: 'Finest fish and veggies',
  //   price: 22.99,
  // },
  // {
  //   id: 'm2',
  //   name: 'Wc-Schnitzel',
  //   description: 'A german specialty!',
  //   price: 16.5,
  // },
  // {
  //   id: 'm3',
  //   name: 'Barbecue Burger-Wc',
  //   description: 'American, raw, meaty',
  //   price: 12.99,
  // },
  // {
  //   id: 'm4',
  //   name: 'Green Wc-Bowl',
  //   description: 'Healthy...and green...',
  //   price: 18.99,
  // },
// ];



const AvailableMeals = () => {
  let DUMMY_MEALS = []
  const fetchData = async function () {
    const response = await fetch('https://react-movies-http-d18ef-default-rtdb.asia-southeast1.firebasedatabase.app/Meals.json')

    const data = await response.json()

    // console.log(data)
    for (const d in data) {
       
      DUMMY_MEALS.push({id: d, name: data[d].name, description: data[d].description,
      price: data[d].price})
    }
  }

  fetchData()

// console.log(DUMMY_MEALS)
  const mealList = DUMMY_MEALS.map(meal => {
  return <MealsItem name={meal.name} desc={meal.description} price={meal.price} id={meal.id} key={meal.id}/>});
  return (
    <section className={style.meals}>
      <Card>
        <ul>
          {mealList}
        </ul>
      </Card>
    </section>
  )
}

export default AvailableMeals;
