import { useState, useEffect } from "react";

const useHttp = async (url) => {
  const [data, setData] = useState([]);
  

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(url);
      const data = await response.json();
      
      let loadedMeals = [];

      for (const d in data) {
        loadedMeals.push(
          { name: data[d].name}
        )
        setData(loadedMeals)
      }

    };
  }, [url]);

  return { data, setData };
};

export default useHttp;
