import React from "react";
import Button from "../UI/Button";
import style from "./Checkout.module.css";
import useInput from "../../hooks/useInput";

const Checkout = (props) => {
  const {
    value: nameValue,
    onBlurHandler: nameOnBlurHandler,
    onChangeInputHnadler: nameChangeHandler,
    hasError: nameHasError,
    setIsTouched: setNameIsTouched,
    valueIsValid: nameIsValid,
    reset: resetName,
  } = useInput((val) => val.trim() !== "");

  const onSubmitHandler = (e) => {
    e.preventDefault();

    setNameIsTouched(true);

    if(!nameIsValid) return;

    resetName()
  };

  return (
    <form className={style.form} onSubmit={onSubmitHandler}>
      <div className={namestyle.control}>
        <label htmlFor="name">Fullname</label>
        <input
          value={nameValue}
          onBlur={nameOnBlurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="name"
        />
        {nameHasError && <p>Please Enter a valid Name!</p>}
      </div>
      <div className={style.control}>
        <label htmlFor="brgy">Barangay</label>
        <input
          value={nameValue}
          onBlur={nameOnBlurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="brgy"
        />
        {nameHasError && <p>Invalid!</p>}
      </div>
      <div className={style.control}>
        <label htmlFor="city">City</label>
        <input
          value={nameValue}
          onBlur={nameOnBlurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="city"
        />
        {nameHasError && <p>Invalid City!</p>}
      </div>
      <div className={style.control}>
        <label htmlFor="province">Province</label>
        <input
          value={nameValue}
          onBlur={nameOnBlurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="province"
        />
        {nameHasError && <p>Invlid Province!</p>}
      </div>
      <div className={style.control}>
        <label htmlFor="postal">Postal Code</label>
        <input
          value={nameValue}
          onBlur={nameOnBlurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="postal"
        />
        {nameHasError && <p>Please Enter a valid Postal Code!</p>}
      </div>
      <div className={style.control}>
        <label htmlFor="contact">Phone/Mobile</label>
        <input
          value={nameValue}
          onBlur={nameOnBlurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="contact"
        />
        {nameHasError && <p>Please Enter a contact!</p>}
      </div>
      
      <div className={style.actions}>
        <Button type="button" onClick={props.onClose}>
          Cancel
        </Button>
        <Button className={style.submit}>Submit</Button>
      </div>
    </form>
  );
};

export default Checkout;
