import { useState, useEffect } from "react";

const useHttp = async (url) => {
  const [data, setData] = useState();

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(url);
      const data = await response.json();
      setData(data);
    };
  }, [url]);

  return { data, setData };
};

export default useHttp;
