import React, { useEffect } from "react";
import style from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealsItem from "./MealsItem/MealsItem";
import useHttp from "../../hooks/useHttp";

const loadedMeals = [];

const AvailableMeals = () => {
  const { fetchData, loading, error } = useHttp();

  useEffect(() => {
    fetchData(
      {
        url: "https://react-movies-http-d18ef-default-rtdb.asia-southeast1.firebasedatabase.app/Meals.json",
      },
      (data) => {
        for (const d in data) {
          loadedMeals.push({
            id: d,
            ...data[d],
          });
        }
      }
    );
  }, [fetchData]);

  if(loading) { 
    loading = <div aria-label="Orange and tan hamster running in a metal wheel" role="img" class="wheel-and-hamster">
    <div class="wheel"></div>
    <div class="hamster">
      <div class="hamster__body">
        <div class="hamster__head">
          <div class="hamster__ear"></div>
          <div class="hamster__eye"></div>
          <div class="hamster__nose"></div>
        </div>
        <div class="hamster__limb hamster__limb--fr"></div>
        <div class="hamster__limb hamster__limb--fl"></div>
        <div class="hamster__limb hamster__limb--br"></div>
        <div class="hamster__limb hamster__limb--bl"></div>
        <div class="hamster__tail"></div>
      </div>
    </div>
    <div class="spoke"></div>
  </div>
  }
  const mealList = loadedMeals.map((meal) => (
    <MealsItem
      name={meal.name}
      desc={meal.description}
      price={meal.price}
      id={meal.id}
      key={meal.id}
    />
  ));
  return (
    <section className={style.meals}>
      {error && (
        <p className={style['error-msg']}>Something went wrong! {error}.</p>
      )}
      {loading }
        <Card>
          <ul>{mealList}</ul>
        </Card>
    </section>
  );
};

export default AvailableMeals;
