import React, { useReducer } from "react";
import CartContext from "./cart-context";

const ACTIONS = {
  ADD_ITEM: "ADD_ITEM",
  REMOVE_ITEM: "REMOVE_ITEM",
};

const defaultCartState = {
  items: [],
  totalAmount: 0,
};

const cartReducer = function (state, action) {
  switch (action.type) {
    case ACTIONS.ADD_ITEM: {
      const totalAmount =
        state.totalAmount + action.payload.amount * action.payload.price;

      const cartItemIndex = state.items.findIndex(
        (item) => item.id === action.payload.id
      );

      const currentItem = state.items[cartItemIndex];

      let updatedItem;
      let updatedItems;

      if (currentItem) {
        updatedItem = [
          {
            ...currentItem,
            amount: currentItem.amount + action.payload.amount,
          },
        ];
        updatedItems = [
          ...state.items,
          (state.items[cartItemIndex] = updatedItem),
        ];
        
      } else {
        updatedItems = []
      }

      return {
        items: [...state.items, action.payload],
        totalAmount: totalAmount,
      };
    }
    case ACTIONS.REMOVE_ITEM: {
      return;
    }
    default:
      return defaultCartState;
  }
};

const CartProvider = (props) => {
  const [state, dispatch] = useReducer(cartReducer, defaultCartState);

  const addCartItemHandler = function (item) {
    dispatch({ type: ACTIONS.ADD_ITEM, payload: item });
  };

  const delCartItemHandler = function (id) {
    dispatch({ type: ACTIONS.REMOVE_ITEM, payload: id });
  };

  const cartContext = {
    items: state.items,
    totalAmount: state.totalAmount,
    addItem: addCartItemHandler,
    delItem: delCartItemHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
