import React from 'react';
import style from './Cart.module.css';
import Modal from '../UI/Modal';


const Cart = (props) => {
const cartItems = <ul className={style['cart-items']}>{[{name: 'sushi', id:'a123'}].map(items => <li key={items.id} >{items.name}</li>)}</ul>;


  return (
    <Modal>
      {cartItems}
      <div className={style.total}>
        <span>Total Amount</span>
        <span>35.60</span>
      </div>
      <div className={style.actions}>
        <button onClick={props.onClose} className={style['button--alt']}>Close</button>
        <button onClick={() => props.onOrder(false)} className={style.button}>Order</button>
      </div>
    </Modal>
  )
}

export default Cart;
