import React, { useState } from "react";
import Header from "./Components/Layout/Header";
import Meals from "./Components/Meals/Meals";
import Cart from "./Components/Cart/Cart";
import CartProvider from "./store/CartProvider";
import useHttp from "./hooks/useHttp";

function App() {
  const [cartModal, setCartModal] = useState(false);

  const cartBtnToggleHandler = function () {
    setCartModal(!cartModal)
  }

  const {data} = useHttp('https://react-movies-http-d18ef-default-rtdb.asia-southeast1.firebasedatabase.app/Meals.json  ')

  return (
    <CartProvider>
      {cartModal && <Cart onClose={cartBtnToggleHandler} onOrder={cartBtnToggleHandler}/>}
      <Header onShowCart={cartBtnToggleHandler} />
      <main>
        <Meals />
      </main>
    </CartProvider>
  );
}

export default App;
