import React, { useState } from "react";
import Header from "./Components/Layout/Header";
import Meals from "./Components/Meals/Meals";
import Cart from "./Components/Cart/Cart";

function App() {
  const [cartModal, setCartModal] = useState(false);
  const cartBtnHandler = function () {
    if (cartModal === false) {
      setCartModal(true);
    } else setCartModal(false);
  };

  const closeModalHandler = function () {
    setCartModal(false)
  }

  const orderBtnHandler = function(e) {
    e.target
    return <a>https://www.facebook.com</a>
  }

  return (
    <>
      {cartModal && <Cart onClose={closeModalHandler} onOrder={orderBtnHandler}/>}
      <Header onClick={cartBtnHandler} />
      <main>
        <Meals />
      </main>
    </>
  );
}

export default App;
