import React, { useRef, useState } from "react";
import Button from "../../UI/Button";
import Input from "../../UI/Input";
import style from "./MealItemForm.module.css";

const MealItemForm = (props) => {
  const [isValid, setIsValid] = useState(true);
  const inputRef = useRef();
  const formSubmitHandler = (e) => {
    e.preventDefault();
const amountInputValue = inputRef.current.value;
const numAmountInputValue = +amountInputValue;

if(amountInputValue.trim() === 0 || numAmountInputValue < 1 || numAmountInputValue > 10) {
  
  return;
}
props.onAddToCart(numAmountInputValue)
  };

  return (
    <form onSubmit={formSubmitHandler} className={style.form}>
      <Input
        label="Amount"
        ref={inputRef}
        input={{
          type: "number",
          id: "amount_" + props.id,
          min: "1",
          max: "10",
          step: "1",
          defaultValue: "1",
        }}
      />
      <Button type="submit" className={style.button} btnLabel="+ Add" />
      {isValid && <p>Please Input Number (1-10) </p>}
    </form>
  );
};

export default MealItemForm;
