import React, { useContext, useState } from "react";
import style from "./Cart.module.css";
import Modal from "../UI/Modal";
import CartContext from "../../store/cart-context";
import Button from "../UI/Button";
import CartItem from "./CartItem";
import Checkout from "./Checkout";
import useHttp from "../../hooks/useHttp";

const Cart = (props) => {
  const [isCheckout, setIsCheckout] = useState(false);
  const [didSubmit, setDidSubmit] = useState(false);
  const { fetchData, loading } = useHttp();
  const cartCtx = useContext(CartContext);

  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const onCartRemoveHandler = (id) => {
    cartCtx.delItem(id);
  };
  const onCartAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  };

  const onSubmitOrderHandler = function (userData) {
    fetchData({
      url: "https://react-movies-http-d18ef-default-rtdb.asia-southeast1.firebasedatabase.app/orders.json",
      method: "POST",
      body: { user: userData, items: cartCtx.items },
    });

    setDidSubmit(true);
  };

  const cartItems = (
    <ul className={style["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={() => onCartRemoveHandler(item.id)}
          onAdd={() => onCartAddHandler(item)}
        />
      ))}
    </ul>
  );
  const cartModalContent = <>{cartItems}
  <div className={style.total}>
    <span>Total Amount</span>
    <span>{totalAmount}</span>
  </div>
  {isCheckout && (
    <Checkout
      onSubmitOrder={onSubmitOrderHandler}
      onClose={props.onClose}
    />
  )}
  {!isCheckout && actionButtons}
  </>
  

  return (
    <Modal onClose={props.onClose}>
      {cartModalContent}
    </Modal>
  );
};

export default Cart;
